class heap(object):
    &#39;&#39;&#39;mode &#x3D;&#x3D; 0 means little heap mode &#x3D;&#x3D; 1 means big heap&#39;&#39;&#39;
    def __init__(self,heap,mode):
        self.__heap &#x3D; []
        self.__mode &#x3D; mode
        for element in heap:
            self.push(element)

    def get_size(self):
        return len(self.__heap)

    def get_top(self):
        if not heap:
            raise IndexError()
        return self.__heap[0]

    def push(self,element):
        p &#x3D; self.get_size()
        self.__heap.append(element)
        if self.__mode &#x3D;&#x3D; 0:
            while p !&#x3D; 0 and self.__heap[(p - 1) &#x2F;&#x2F; 2] &gt; self.__heap[p]:
                self.__heap[(p - 1) &#x2F;&#x2F; 2],self.__heap[p] &#x3D; self.__heap[p],self.__heap[(p - 1) &#x2F;&#x2F; 2]
                p &#x3D; (p - 1) &#x2F;&#x2F; 2
