public class BinarySearch &#123;
    //è¿­ä»£é€’å½’çš„æ—¶é—´å¤æ‚åº¦éƒ½æ˜¯O(logn)
    //é€’å½’çš„ç©ºé—´å¤æ‚åº¦ä¸ºO(logn) è¿­ä»£ä¸ºO(1)
    public static void main(String[] args) &#123;
        int[] array = new int[]&#123;1, 2, 3, 4, 5, 5, 6&#125;;
        class Search &#123;
            //[l,r)
            public int searchIndexIter(int[] array, int l, int r, int tar) &#123;
                int mid;
                while (l &lt; r) &#123;
                    mid = l + ((r - l) >> 1);
                    if (array[mid] >= tar) &#123;
                        r = mid;
                    &#125; else &#123;
                        // arrya[mid] &lt; tar
                        //at least mid + 1
                        l = mid + 1;
                    &#125;
                &#125;
                return l;
            &#125;

            public int searchIndexRec(int[] array, int l, int r, int tar) &#123;
                if (l >= r) &#123;
                    return l;
                &#125;
                int mid = l + ((r - l) >> 1);
                if (array[mid] >= tar) &#123;
                    return searchIndexRec(array, l, mid, tar);
                &#125; else &#123;
                    return searchIndexRec(array,mid + 1,r,tar);
                &#125;
            &#125;
        &#125;
        Search search = new Search();
        int ans = search.searchIndexIter(array,0,array.length,5);
        System.out.println(ans);
        ans = search.searchIndexRec(array, 0, array.length, 4);
        System.out.println(ans);
    &#125;
